DROP TABLE IF EXISTS `sbg_blog`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `sbg_blog` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自动编号',
  `cids` varchar(20) NOT NULL COMMENT '分类IDs',
  `title` varchar(127) NOT NULL COMMENT '博客标题',
  `iscomment` tinyint(4) NOT NULL DEFAULT '1' COMMENT '是否允许评论',
  `isrecommend` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `ctype` tinyint(4) NOT NULL DEFAULT '0' COMMENT '内容类型（单面/页面集）',
  `author` int(11) NOT NULL,
  `hits` int(11) NOT NULL COMMENT '点击量',
  `pubdate` int(11) NOT NULL COMMENT '创建时间',
  `modify` int(11) NOT NULL COMMENT '更新时间',
  `tags` varchar(100) DEFAULT NULL COMMENT '标签',
  `status` int(11) NOT NULL DEFAULT '1' COMMENT '博文状态',
  `content` text NOT NULL COMMENT '博文内容',
  PRIMARY KEY (`id`),
  KEY `author` (`author`),
  CONSTRAINT `sbg_blog_ibfk_1` FOREIGN KEY (`author`) REFERENCES `simplebase`.`sb_user` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=220 DEFAULT CHARSET=utf8;
