-14170|-14159|-14151|-14149\".\r\n		\"|-14145|-14140|-14137|-14135|-14125|-14123|-14122|-14112|-14109|-14099|-14097|-14094|-14092|-14090|-14087\".\r\n		\"|-14083|-13917|-13914|-13910|-13907|-13906|-13905|-13896|-13894|-13878|-13870|-13859|-13847|-13831|-13658\".\r\n		\"|-13611|-13601|-13406|-13404|-13400|-13398|-13395|-13391|-13387|-13383|-13367|-13359|-13356|-13343|-13340\".\r\n		\"|-13329|-13326|-13318|-13147|-13138|-13120|-13107|-13096|-13095|-13091|-13076|-13068|-13063|-13060|-12888\".\r\n		\"|-12875|-12871|-12860|-12858|-12852|-12849|-12838|-12831|-12829|-12812|-12802|-12607|-12597|-12594|-12585\".\r\n		\"|-12556|-12359|-12346|-12320|-12300|-12120|-12099|-12089|-12074|-12067|-12058|-12039|-11867|-11861|-11847\".\r\n		\"|-11831|-11798|-11781|-11604|-11589|-11536|-11358|-11340|-11339|-11324|-11303|-11097|-11077|-11067|-11055\".\r\n		\"|-11052|-11045|-11041|-11038|-11024|-11020|-11019|-11018|-11014|-10838|-10832|-10815|-10800|-10790|-10780\".\r\n		\"|-10764|-10587|-10544|-10533|-10519|-10331|-10329|-10328|-10322|-10315|-10309|-10307|-10296|-10281|-10274\".\r\n		\"|-10270|-10262|-10260|-10256|-10254\";\r\n	}\r\n	private function _Array_Combine($_Arr1, $_Arr2)\r\n	{\r\n		for($i=0; $i&lt;count($_arr1); $i++)=\"\" $_res[$_arr1[$i]]=\"$_Arr2[$i];\" return=\"\" $_res;=\"\" }=\"\" private=\"\" function=\"\" _u2_utf8_gb($_c)=\"\" {=\"\" $_string=\"\" ;=\"\" if($_c=\"\" &lt;=\"\" 0x80)=\"\" .=\"$_C;\" elseif($_c=\"\" 0x800)=\"\" |=\"\" $_c=\"\"&gt;&gt;6);\r\n			$_String .= chr(0x80 | $_C &amp; 0x3F);\r\n		}elseif($_C &lt; 0x10000){\r\n			$_String .= chr(0xE0 | $_C&gt;&gt;12);\r\n			$_String .= chr(0x80 | $_C&gt;&gt;6 &amp; 0x3F);\r\n			$_String .= chr(0x80 | $_C &amp; 0x3F);\r\n		} elseif($_C &lt; 0x200000) {\r\n			$_String .= chr(0xF0 | $_C&gt;&gt;18);\r\n			$_String .= chr(0x80 | $_C&gt;&gt;12 &amp; 0x3F);\r\n			$_String .= chr(0x80 | $_C&gt;&gt;6 &amp; 0x3F);\r\n			$_String .= chr(0x80 | $_C &amp; 0x3F);\r\n		}\r\n		return iconv(\'UTF-8\', \'GB2312\', $_String);\r\n	}\r\n	private function _Pinyin($_Num, $_Data)\r\n	{\r\n		if ($_Num&gt;0 &amp;&amp; $_Num&lt;160 )\r\n			return chr($_Num);\r\n		elseif($_Num&lt;-20319 || $_Num&gt;-10247) \r\n			return \'\';\r\n		else {\r\n			foreach($_Data as $k=&gt;$v){ if($v&lt;=$_Num) break; }\r\n			return $k;\r\n		}\r\n	}\r\n	public function words2PinYin($_String, $_Code=\'gb2312\')\r\n	{\r\n		$_TDataKey = explode(\'|\', $this-&gt;_DataKey);\r\n		$_TDataValue = explode(\'|\', $this-&gt;_DataValue);\r\n		$_Data = (PHP_VERSION&gt;=\'5.0\') ? array_combine($_TDataKey, $_TDataValue) : $this-&gt;_Array_Combine($_TDataKey, $_TDataValue);\r\n		arsort($_Data);\r\n		reset($_Data);\r\n		if($_Code != \'gb2312\') $_String = $this-&gt;_U2_Utf8_Gb($_String);\r\n		$_Res = \'\';\r\n		for($i=0; $i&lt;strlen($_string); $i++)=\"\" {=\"\" $_p=\"ord(substr($_String,\" $i,=\"\" 1));=\"\" if($_p=\"\"&gt;160) { $_Q = ord(substr($_String, ++$i, 1)); $_P = $_P*256 + $_Q - 65536; }\r\n			$_Res .= $this-&gt;_Pinyin($_P, $_Data);\r\n		}\r\n		return preg_replace(\"/[^a-z0-9]*/\", \'\', $_Res);\r\n	}\r\n}<br />下面是使用方法：<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; $pinYin = new PinYin();<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;echo $pinYin-&gt;words2PinYin(\'这是小超的网站，欢迎访问http://www.eb163.com\'); //默认是gb编码<br />&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;echo $pinYin-&gt;words2PinYin(\'会东\',1); //第二个参数随意设置即为utf8编码<br /></pre></div><br />'),(92,'2','用css调用外部字体',1,1,1,1,537,1324819555,1397284385,' 字体,文件,适用于,只是,浏览器',1,'* .TTF或.OTF，适用于Firefox 3.5、Safari、Opera <br />\r\n* .EOT，适用于Internet Explorer 4.0+ <br />\r\n* .SVG，适用于Chrome、IPhone <br />\r\n比如:<br /><div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">css代码</div><pre class=\"brush:css;\">@font-face {\r\n    font-family: \'HansHandItalic\';\r\n    src: url(\'fonts/hanshand-webfont.eot\');\r\n    src: url(\'fonts/hanshand-webfont.eot?#iefix\') format(\'embedded-opentype\'),\r\n         url(\'fonts/hanshand-webfont.woff\') format(\'woff\'),\r\n         url(\'fonts/hanshand-webfont.ttf\') format(\'truetype\'),\r\n         url(\'fonts/hanshand-webfont.svg#webfont34M5alKg\') format(\'svg\');\r\n    font-weight: normal;\r\n    font-style: normal;\r\n}</pre></div><br />\r\n自己提供字体的各种版本，浏览器自动下载相应字体文件<br />\r\n如果是英文字体，可以在<a href=\"http://www.google.com/webfonts\" target=\"_blank\">http://www.google.com/webfonts</a>找到的话，由google生成相应code，由google提供在线字体以及浏览器兼容<br />\r\n<br />\r\n如果是中文字体的话，建议还是用图片代替，因为中文字体文件一般都很大，<br />\r\n除非只选择需要的中文字符生成字体文件（比如说只是网站导航栏菜单的字符），<br />\r\n有在线工具帮我们实现这个，比如<a href=\"http://www.fontsquirrel.com/fontface/generator\" target=\"_blank\">http://www.fontsquirrel.com/fontface/generator</a><br />\r\n只是有的是英文不懂操作，有的限制上传字体文件大小<br />\r\n<br />\r\n还有基于js的cufon、typeface、typekit<br />'),(93,'2','Yii 验证码使用',1,1,1,1,721,1325560466,1397064795,'verifyCode',1,'<div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">php代码</div><pre class=\"brush:php;\">//这是控制器\r\npublic function actions() {\r\n    return array(\r\n        \'captcha\' =&gt; array(\r\n            \'class\' =&gt; \'CCaptchaAction\',\r\n            \'backColor\' =&gt; 0xFFFFFF, //背景颜色\r\n             \'minLength\' =&gt; 4, //最短为4位\r\n             \'maxLength\' =&gt; 8, //最长为8位\r\n             \'transparent\' =&gt; true, //显示为透明\r\n        ),\r\n    );\r\n}\r\n \r\n//这是模型\r\npublic $verifyCode;\r\n \r\npublic function rules() {\r\n    return array(\r\n        array(\'verifyCode\' , \'captcha\' , \'allowEmpty\' =&gt; !CCaptcha::checkRequirements()),\r\n    );\r\n}\r\n \r\n//这是试图\r\n<!--?<a title=\"See also 双冒号 PHP范围解析操作符\" href=\"http://www.php8.org/archives/58.html\"-- >php if(CCaptcha::checkRequirements(): ?&gt;\r\n<div >\r\n        <!--?php echo $form--->labelEx($model,\'verifyCode\'); ?&gt;\r\n        <div>\r\n        <!--?php $this--->widget(\'CCaptcha\',array(\'clickableImage\'=&gt;true,\'showRefreshButton\'=&gt;false)); ?&gt;\r\n        <!--?php echo $form--->textField($model,\'verifyCode\'); ?&gt;\r\n        </div>\r\n        <div>Please enter the letters as they are shown in the image above.\r\n        <br />Letters are not case-sensitive.</div>\r\n        <!--?php echo $form--->error($model , \'verifyCode\');?&gt;\r\n</pre></div>\r\n<!--?php endif; ?-->\r\n//此处的clickableImage =&gt; true 设置点击图片可以刷新验证码，默认是false\r\n//此处的showRequirements =&gt; false 设置不出现刷新验证码的链接，默认是true<br />'),(94,'2','Poshy Tip',1,1,1,1,371,1325623810,1397280047,' 插件,位置,气泡,外观,工具',1,'<p>很好用的一个jquery 冒泡插件</p><p>Poshy Tip 是一个强大的jQuery 工具提示插件，拥有不同的外观。作为 Form Tooltips使用时，可以自定义气泡出现的位置。</p><p>在线演示：<a href=\"http://vadikom.com/demos/poshytip/\" target=\"_blank\">http://vadikom.com/demos/poshytip/</a><br /></p><br />'),(95,'2','美化select下拉菜单--jQuery插件chosen',1,1,1,1,405,1325656940,1397297475,' 效果,事件,插件,啤酒,属性',1,'<p>Chosen 是一个JavaScript插件，它能让丑陋的、很长的select选择框变的更好看、更方便。目前，它支持 jQuery 和 Prototype 两种JavaScript引擎。</p><p><a href=\"https://github.com/harvesthq/chosen/\">在GitHub上的官网</a></p>'),(96,'2','array_map函数在PHP类中调用内部方法简介',1,1,1,1,351,1325661223,1397065010,' array_mpa(),函数,数组,单元,方法,代码',1,'在PHP编程中，我们经常会遇到处理数组的单元数据问题，比如对数组中每个单元应用自定义函数。<br />\r\n<br />\r\n一种方法是通过循环遍历整个数组，对每个单元调用自定义函数，然后用返回值替换原数组相应单元的值。这也是最常见和简单的方法，在此就不举例了。<br />\r\n<br />\r\n一种方法是通过PHP提供的array_map函数回调自定义函数，这也是被推荐的方法。<br />\r\n<br />\r\n<div class=\"refsect1\"><em><span style=\"color:#0000ff;\">array_map</span> -- 将回调函数作用到给定数组的单元上<br />\r\n<br />\r\n说明:<br />\r\n<br />\r\narray <span style=\"color:#0000ff;\">array_map</span> ( callback callback, array arr1 [, array ...] )<br />\r\n<br />\r\n<span style=\"color:#0000ff;\">array_map</span>() 返回一个数组，该数组包含了 arr1 中的所有单元经过 callback 作用过之后的单元。callback 接受的参数数目应该和传递给 array_map() 函数的数组数目一致。</em></div>\r\n<br /><p>\r\n摘录一段PHP手册的列子简要说明如下：</p><p><div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">php代码</div><pre class=\"brush:php;\"><!--?php\r\nfunction cube($n){\r\nreturn $n * $n * $n;\r\n}\r\n$a = array(1, 2, 3, 4, 5);\r\n$b = array_map(\"cube\", $a);\r\nprint_r($b);\r\n?--> <em><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">&lt;?php<br />\r\n</span><span style=\"color:#007700;\">function </span><span style=\"color:#0000bb;\">cube</span><span style=\"color:#007700;\">(</span><span style=\"color:#0000bb;\">$n</span><span style=\"color:#007700;\">){<br />\r\nreturn </span><span style=\"color:#0000bb;\">$n </span><span style=\"color:#007700;\">* </span><span style=\"color:#0000bb;\">$n </span><span style=\"color:#007700;\">*  </span><span style=\"color:#0000bb;\">$n</span><span style=\"color:#007700;\">;<br />\r\n}<br />\r\n</span><span style=\"color:#0000bb;\">$a </span><span style=\"color:#007700;\">= array(</span><span style=\"color:#0000bb;\">1</span><span style=\"color:#007700;\">,  </span><span style=\"color:#0000bb;\">2</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">3</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">4</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">5</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">$b  </span><span style=\"color:#007700;\">= </span><span style=\"color:#0000bb;\">array_map</span><span style=\"color:#007700;\">(</span><span style=\"color:#dd0000;\">\"cube\"</span><span style=\"color:#007700;\">,  </span><span style=\"color:#0000bb;\">$a</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">print_r</span><span style=\"color:#007700;\">(</span><span style=\"color:#0000bb;\">$b</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">?&gt;</span> </span></code></em></pre></div><br /></p>那么，如果你是在一个PHP类中通过array_map函数回调内部方法又该如何做呢？<br />\r\n<br />\r\n同样，我们可以在PHP手册中找到一段用户添加的说明：<br />\r\n<br />\r\n<code><span class=\"php_000000\">If you need to call a static method from array_map,  this will NOT work:<br />\r\n（如果你想在array_map函数中回调一个静态方法，那么下面的做法是错误的）<br />\r\n<br />\r\n</span></code><em><code></code></em><div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">php代码</div><pre class=\"brush:php;\"><!--?php\r\n$a = array(1, 2, 3, 4, 5);\r\n$b = array_map(\"myclass::myMethoed\", $a);\r\nprint_r($b);\r\n?--><em><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">&lt;?php</span><span style=\"color:#007700;\"><br />\r\n</span><span style=\"color:#0000bb;\">$a </span><span style=\"color:#007700;\">= array(</span><span style=\"color:#0000bb;\">1</span><span style=\"color:#007700;\">,  </span><span style=\"color:#0000bb;\">2</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">3</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">4</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">5</span><span style=\"color:#007700;\">);<br />\r\n</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">$b </span><span style=\"color:#007700;\">= </span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">array_map</span><span style=\"color:#007700;\">(</span><span style=\"color:#dd0000;\">\"myclass::myMethoed\"</span><span style=\"color:#007700;\">,  </span><span style=\"color:#0000bb;\">$a</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">print_r</span><span style=\"color:#007700;\">(</span><span style=\"color:#0000bb;\">$b</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">?&gt;</span> </span></code></em><code><span class=\"php_000000\"><span class=\"php_0000BB\"><br />\r\n</span></span></code>\r\n</pre></div><code><span class=\"php_000000\"><span class=\"php_0000BB\"><br />\r\n</span><br />\r\nInstead, you need to do this:<br />\r\n（你应该做如下调用）<br />\r\n<br />\r\n</span></code><em><code></code></em><div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">php代码</div><pre class=\"brush:php;\"><!--?php\r\n$a = array(1, 2, 3, 4, 5);\r\n$b = array_map(array(\"myclass\",\"myMethoed\"), $a);\r\nprint_r($b);\r\n?--> <em><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">&lt;?php</span><span style=\"color:#007700;\"><br />\r\n</span><span style=\"color:#0000bb;\">$a </span><span style=\"color:#007700;\">= array(</span><span style=\"color:#0000bb;\">1</span><span style=\"color:#007700;\">,  </span><span style=\"color:#0000bb;\">2</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">3</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">4</span><span style=\"color:#007700;\">, </span><span style=\"color:#0000bb;\">5</span><span style=\"color:#007700;\">);<br />\r\n</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">$b </span><span style=\"color:#007700;\">= </span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">array_map</span><span style=\"color:#007700;\">(</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#0000bb;\">array</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#007700;\">(</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#dd0000;\">\"myclass\",\"myMethoed\"</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#007700;\">)</span></span></code><code><span style=\"color:#000000;\"><span style=\"color:#007700;\">,  </span><span style=\"color:#0000bb;\">$a</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">print_r</span><span style=\"color:#007700;\">(</span><span style=\"color:#0000bb;\">$b</span><span style=\"color:#007700;\">);<br />\r\n</span><span style=\"color:#0000bb;\">?&gt;</span> </span></code></em></pre></div><br />'),(97,'2','微博心事-2012-01-12',1,0,1,1,298,1326349779,1397078254,NULL,1,'今天下午开完会，这一年就over了，当然接下来呢，自然也应该只是天天呆在家里，完成我的设计。'),(98,'2','PHP中的Array2String & String2Array',1,1,1,1,501,1326408718,1397277510,' 数组,这时,数据库,时候,页面',1,'<div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">php代码</div><pre class=\"brush:php;\"><!--?/*在Array和String类型之间转换，转换为字符串的数组可以直接在URL上传递*/\r\n// convert a multidimensional array to url save and encoded string\r\n// usage: string Array2String( array Array )\r\nfunction Array2String($Array)\r\n{\r\n    $Return=\'\';\r\n    $NullValue=\"^^^\";\r\n    foreach ($Array as $Key =--> $Value) {\r\n        if(is_array($Value))\r\n            $ReturnValue=\'^^array^\'.Array2String($Value);\r\n        else\r\n            $ReturnValue=(strlen($Value)&gt;0)?$Value:$NullValue;\r\n        $Return.=urlencode(base64_encode($Key)) . \'|\' . urlencode(base64_encode($ReturnValue)).\'||\';\r\n    }\r\n    return urlencode(substr($Return,0,-2));\r\n}\r\n// convert a string generated with Array2String() back to the original (multidimensional) array\r\n// usage: array String2Array ( string String)\r\nfunction String2Array($String)\r\n{\r\n    $Return=array();\r\n    $String=urldecode($String);\r\n    $TempArray=explode(\'||\',$String);\r\n    $NullValue=urlencode(base64_encode(\"^^^\"));\r\n    foreach ($TempArray as $TempValue) {\r\n        list($Key,$Value)=explode(\'|\',$TempValue);\r\n        $DecodedKey=base64_decode(urldecode($Key));\r\n        if($Value!=$NullValue) {\r\n            $ReturnValue=base64_decode(urldecode($Value));\r\n            if(substr($ReturnValue,0,8)==\'^^array^\')\r\n                $ReturnValue=String2Array(substr($ReturnValue,8));\r\n            $Return[$DecodedKey]=$ReturnValue;\r\n        }\r\n        else\r\n        $Return[$DecodedKey]=NULL;\r\n    }\r\n    return $Return;\r\n}\r\n?&gt;</pre></div>在用PHP开发时我们有时期望将一个数组（任意多维），在页面之间传递或者存入数据库。这时我们可以将Array转换为String传递或保存，取出用的时候在转换回来即可。<br />'),(99,'2','jquery uploadify插件可选参数的介绍 ',1,1,1,1,408,1327460709,1397105029,' 文件,参数,函数,路径,按钮',1,'jquery uploadify插件可选参数的介绍\r\n            \r\n			<div id=\"appShareOpt\">\r\n                \r\n                            </div>\r\n            <div id=\"app-share-content\">\r\n	                    <p>\r\n</p><table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>1</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">uploader</span></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p>上传控件的主体文件，flash控件</p>\r\n            <p> <wbr> <wbr> 默认值=\'uploadify.swf\'</wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>2</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">script</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>相对路径的后端脚本，它将处理您上传的文件。</strong></p>\r\n            <p><strong>绝对路径前缀或\'/\'或\'http\'的路径</strong></p>\r\n            <p><strong> <wbr> 默认值=\'uploadify.php\'</wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>3</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>checkScript</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p>检查该文件是否已经选择驻留在服务器上。<br />\r\n            没有默认值。 官方例子中\'check.php\'是提供核心文件</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>4</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">scriptData</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>可提供URL传递参数。用来传递get参数。例如：</strong></p>\r\n            <p><strong> <wbr> index.jsp?id=1&amp;action=uploadify可以设置成：</wbr></strong></p>\r\n            <p><span style=\"color:#ff0000;\"><strong>\'script\': \'index.jsp\',</strong></span></p>\r\n            <p><span style=\"color:#ff0000;\"><strong>\'scriptData\':{\'id\':1,\'action\':\'uploadify\'},</strong></span></p>\r\n            <p><span style=\"color:#ff0000;\"><strong>注：要设置‘method’：‘GET’.</strong></span></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>5</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>fileDataName</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>您的文件在上传服务器脚本阵列的名称。<br />\r\n             <wbr> <wbr> 默认值=\'Filedata\'</wbr></wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>6</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>method</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>设置为发送到后端脚本的方法。要么\'get\'或post\'。</wbr></p>\r\n            <p> <wbr> <wbr> 默认值\'post\'</wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>7</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><a name=\"OLE_LINK4\"><strong><u><span style=\"color:#6d5887;\">scriptAccess</span></u></strong></a></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong> <wbr>?</wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>8</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">folder</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p>您想将文件保存到的路径。考虑到安全问题，一般并不在客户端设定后供服务器得到所存的路径。我试了下。<span style=\"color:#ff0000;\">这个参数好像以get的方式传递的。设定post得不到这个值。</span></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>9</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>queueID</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>文件队列ID。与div的id一致。参考上一篇例子的用法。</wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>10</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">queueSizeLimit</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>限制在一次队列中的次数（可选定几个文件）。默认值= 999，而一次可传几个文件有 simUploadLimit属性决定</strong>。</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>11</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">multi</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>是否允许同时上传多文件，可设定true或false。</strong></p>\r\n            <p><strong> <wbr> 默认false。设定true时，选中的文件是当前项。</wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>12</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">auto</span></strong></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>选定文件后是否自动上传，可设定true或false。</strong></p>\r\n            <p><strong> <wbr> 默认false</wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>13</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">fileDesc</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>出现在上传对话框中的文件类型描述。与fileExt需同时使用</strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>14</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">fileExt</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>支持的格式，启用本项时需同时声明fileDesc。</strong></p>\r\n            <p><strong>如：‘*.rar,*.doc</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>15</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">sizeLimit</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>控制上传文件的大小，单位byte</strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>16</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">simUploadLimit</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>多文件上传时，同时上传文件数目限制。默认1</strong></p>\r\n            <p><strong> <wbr> 一次可传几个文件。</wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>17</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong><span style=\"color:#ff0000;\">buttonText</span></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>默认按钮的名字。默认BROWER</strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>18</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><a name=\"OLE_LINK6\"></a><a name=\"OLE_LINK5\"><strong><u><span style=\"color:#6d5887;\">buttonImg</span></u></strong></a></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p>使用图片按钮，设定图片的路径即可。</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>19</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>hideButton</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p>上传按钮的隐藏。true 或false。默认flase</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>20</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>rollover</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong> <wbr>？</wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>21</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>width</strong></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>按钮图片的长度。默认 110</wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>22</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>height</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>按钮图片的高度。默认 30</wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>23</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>wmode</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>背景透明transparent 与不透明opaque设定。默认 不透明</wbr></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>24</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>cancelImg</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p> <wbr>取消按钮。设定图片路径。默认cancel.png</wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>25</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onInit</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\">函数， 初始化时的状态。</p>\r\n            <p align=\"left\">onInit: function() { <wbr></wbr></p>\r\n            <p align=\"left\">$(\"#id\").html(\"上传前\");},</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>26</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onComplete</strong></p>\r\n            <p><u><strong> <wbr></wbr></strong></u></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><a name=\"OLE_LINK11\"><strong><u><span style=\"color:#6d5887;\">函数：可传递五个参数</span></u></strong></a></p>\r\n            <p align=\"left\"><strong><span style=\"color:#ff0000;\">event</span>:</strong> 事件对象</p>\r\n            <p align=\"left\"><strong><span style=\"color:#ff0000;\">queueID</span>:</strong> 完成文件的唯一标识符。</p>\r\n            <p align=\"left\"><strong><span style=\"color:#ff0000;\">fileObj</span>:</strong>  <wbr></wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>name</strong> – 文件名</wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>filepath</strong> –上传路径</wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>size</strong> – 文件大小</wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>creationDate</strong> – 文件创建时间</wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>modificationDate</strong> –<a name=\"OLE_LINK9\"></a><a name=\"OLE_LINK8\"><u><span style=\"color:#6d5887;\">文件最近修改时间</span></u></a></wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>type</strong> –文件的扩展名</wbr></p>\r\n            <p align=\"left\"><span style=\"color:#ff0000;\">response</span><strong>:</strong> 服务器回调的数据</p>\r\n            <p align=\"left\"><span style=\"color:#ff0000;\">data</span><strong>:</strong>  <wbr></wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>fileCount</strong> – The total number of files left in the queue</wbr></p>\r\n            <p align=\"left\"> <wbr> • <strong>speed</strong> – 平均上传速度 KB/s</wbr></p>\r\n            <p align=\"left\"><strong><span style=\"color:#ff0000;\">如：</span></strong></p>\r\n            <p align=\"left\"><span style=\"color:#ff0000;\"><strong>onComplete</strong>: function(event, queueID, fileObj) {</span></p>\r\n            <p><span style=\"color:#ff0000;\"> <wbr> alert(\"文件:\" +fileObj.name + \"上传失败\"); <wbr> <wbr>}</wbr></wbr></wbr></span></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>27</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onSelectOnce</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><strong>函数：可传递二个参数</strong></p>\r\n            <p><strong><span style=\"color:#ff0000;\">event</span>:</strong> The event object.</p>\r\n            <p><strong><span style=\"color:#ff0000;\">data</span>:</strong> An object containing details about the select operation.</p>\r\n            <p> <wbr> • <strong>fileCount</strong> – The total number of files in the queue</wbr></p>\r\n            <p> <wbr> • <strong>filesSelected</strong> – The number of files selected in the select operation</wbr></p>\r\n            <p> <wbr> • <strong>filesReplaced</strong> – The number of files that were replaced in the queue</wbr></p>\r\n            <p> <wbr> • <strong>allBytesTotal</strong> – The total number of bytes for all files in the queue</wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>28</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onCancel</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><strong>函数：可传递四个参数</strong></p>\r\n            <p><strong><span style=\"color:#ff0000;\">event</span>:</strong> The event object.</p>\r\n            <p><strong><span style=\"color:#ff0000;\">queueID</span>:</strong> The unique identifier of the file that was cancelled.</p>\r\n            <p><strong><span style=\"color:#ff0000;\">fileObj</span>:</strong> An object containing details about the file that was selected.</p>\r\n            <p> <wbr> <wbr> • <strong>name</strong> – The name of the file</wbr></wbr></p>\r\n            <p> <wbr> <wbr> • <strong>size</strong> – The size in bytes of the file</wbr></wbr></p>\r\n            <p> <wbr> <wbr> • <strong>creationDate</strong> – The date the file was created</wbr></wbr></p>\r\n            <p> <wbr> <wbr> • <strong>modificationDate</strong> – The last date the file was modified</wbr></wbr></p>\r\n            <p> <wbr> <wbr> • <strong>type</strong> – The file extension beginning with a \'.\'</wbr></wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            <p><strong><span style=\"color:#ff0000;\">data</span>:</strong> Details about the file queue.</p>\r\n            <p> <wbr> <wbr> • <strong>fileCount</strong> – The total number of files left in the queue</wbr></wbr></p>\r\n            <p> <wbr> <wbr> • <strong>allBytesTotal</strong> – The total number of bytes left for all files in the queue</wbr></wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>29</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onClearQueue</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><strong>函数：可传递一个参数</strong></p>\r\n            <p><strong>event:</strong> The event object.</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>30</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onQueueFull</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><strong>函数：可传递二个参数</strong></p>\r\n            <p>• <strong>event</strong> - The event object.</p>\r\n            <p>• <strong>queueSizeLimit</strong> - The maximum size of the queue.</p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>31</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onError</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><a name=\"OLE_LINK13\"></a><a name=\"OLE_LINK7\"></a><a name=\"OLE_LINK10\"><strong><u><span style=\"color:#6d5887;\">函数：可传递四个参数</span></u></strong></a></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            <p><strong><span style=\"color:#ff0000;\">event</span></strong>: The event object.</p>\r\n            <p><strong><span style=\"color:#ff0000;\">queueID</span>:</strong> The unique identifier of the file that was errored.</p>\r\n            <p><strong><span style=\"color:#ff0000;\">fileObj</span>:</strong> An object containing details about the file that was selected.</p>\r\n            <p> <wbr> • <strong>name</strong> – The name of the file</wbr></p>\r\n            <p> <wbr> • <strong>size</strong> – The size in bytes of the file</wbr></p>\r\n            <p> <wbr> • <strong>creationDate</strong> – The date the file was created</wbr></p>\r\n            <p> <wbr> • <strong>modificationDate</strong> – The last date the file was modified</wbr></p>\r\n            <p> <wbr> <strong>• type</strong> – The file extension beginning with a \'.\'</wbr></p>\r\n            <p>errorObj: An object containing details about the error returned.</p>\r\n            <p> <wbr> <strong>• type</strong> – Either \'HTTP\', \'IO\', or \'Security\'</wbr></p>\r\n            <p> <wbr> <strong>• info</strong> – An error message describing the type of error returned</wbr></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>32</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onOpen</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>函数：可传递三个参数</strong></p>\r\n            <p><strong>event:</strong> The event object.</p>\r\n            <p><strong>queueID:</strong> The unique identifier of the file that was opened.</p>\r\n            <p><strong>fileObj:</strong> An object containing details about the file that was selected.</p>\r\n            <p> <wbr> • <strong>name</strong> – The name of the file</wbr></p>\r\n            <p> <wbr> • <strong>size</strong> – The size in bytes of the file</wbr></p>\r\n            <p> <wbr> • <wbr><strong>creationDate</strong> – The date the file was created</wbr></wbr></p>\r\n            <p> <wbr> • <strong>modificationDate</strong> – The last date the file was modified</wbr></p>\r\n            <p> <wbr> • <strong>type</strong> – The file extension beginning with a \'.\'</wbr></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>33</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onProgress</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>函数：可传递四个参数</strong></p>\r\n            <p><strong>event:</strong> The event object.</p>\r\n            <p><strong>queueID:</strong> The unique identifier of the file that was updated.</p>\r\n            <p><strong>fileObj:</strong> An object containing details about the file that was selected.</p>\r\n            <p> <wbr> • <strong>name</strong> – The name of the file</wbr></p>\r\n            <p> <wbr> • <strong>size</strong> – The size in bytes of the file</wbr></p>\r\n            <p> <wbr> • <strong>creationDate</strong> – The date the file was created</wbr></p>\r\n            <p> <wbr> • <strong>modificationDate</strong> – The last date the file was modified</wbr></p>\r\n            <p> <wbr> • <strong>type</strong> – The file extension beginning with a \'.\'</wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            <p><strong>data:</strong> An object containing details about the upload and queue.</p>\r\n            <p> <wbr> • <strong>percentage</strong> – The current percentage completed for the upload</wbr></p>\r\n            <p> <wbr> • <strong>bytesLoaded</strong> – The current amount of bytes uploaded</wbr></p>\r\n            <p> <wbr> • <strong>allBytesLoaded</strong> – The current amount of bytes loaded for all files in the queue</wbr></p>\r\n            <p> <wbr> • <strong>speed</strong> – The current upload speed in KB/s</wbr></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>34</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onSelect</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p><strong>event:</strong> The event object.</p>\r\n            <p><strong>queueID:</strong> The unique identifier of the file that was selected.</p>\r\n            <p><strong>fileObj:</strong> An object containing details about the file that was selected.</p>\r\n            <p> <wbr> • <strong>name</strong> – The name of the file</wbr></p>\r\n            <p> <wbr> • <strong>size</strong> – The size in bytes of the file</wbr></p>\r\n            <p> <wbr> • <strong>creationDate</strong> – The date the file was created</wbr></p>\r\n            <p> <wbr> • <strong>modificationDate</strong> – The last date the file was modified</wbr></p>\r\n            <p> <wbr> • <strong>type</strong> – The file extension beginning with a \'.\'</wbr></p>\r\n            <p align=\"left\"> <wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>35</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onAllComplete</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><a name=\"OLE_LINK12\"><strong><u><span style=\"color:#6d5887;\">函数：可传递二个参数</span></u></strong></a></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            <p><strong>event:</strong> The event object.</p>\r\n            <p><strong>data:</strong> An object containing details about the upload process.</p>\r\n            <p> <wbr> • <strong>filesUploaded</strong> – The total number of files uploaded</wbr></p>\r\n            <p> <wbr> • <strong>errors</strong> – The total number of errors while uploading</wbr></p>\r\n            <p> <wbr> • <strong>allbytesLoaded</strong> – The total number of bytes uploaded</wbr></p>\r\n            <p> <wbr> • <strong>speed</strong> – The average speed of all uploaded files</wbr></p>\r\n            <p> <wbr></wbr></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td valign=\"top\" width=\"40\">\r\n            <p><strong>36</strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"149\">\r\n            <p><strong>onCheck</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            </td>\r\n            <td valign=\"top\" width=\"380\">\r\n            <p align=\"left\"><strong>函数：可传递五个参数</strong></p>\r\n            <p><strong> <wbr></wbr></strong></p>\r\n            <p><strong>event:</strong> The event object.</p>\r\n            <p><strong>checkScript:</strong> The path to the file checking script.</p>\r\n            <p><strong>fileQueue:</strong> A file queue object consisting of <wbr> key/value pairs with the queue ID as the key and the filename as the value.</wbr></p>\r\n            <p><strong>folder:</strong> The path to the upload folder.</p>\r\n            <p><strong>single:</strong> True if only one file is being uploaded from the queue.</p>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<p>装载于：<a>http://blog.sina.com.cn/s/blog_5db0dfe40100ielj.html</a></p>\r\n            			</div><br />'),(100,'2','jquery uploadify(转) ',1,1,1,1,456,1327460798,1396595359,' 方法,编码,图片,原因,中文',1,'<div id=\"app-share-content\">\r\n	                    <p>应公司编辑部要求，需要图片批量上传功能来提高工作效率。在网上收集了些资料后，觉得JQuery Uploadify插件不错。下面简要介绍下Uploadify在ASP.NET中的实现以及遇到的一些问题</p><p>&nbsp;&nbsp;&nbsp; 首先介绍下实现上传功能</p><p>&nbsp;&nbsp;&nbsp; 1. 下载Uploadify插件：<a target=\"_blank\">官方下载</a>(本人以2.1.4版本开发）</p><p>&nbsp;&nbsp;&nbsp; 2. 创建一个Web项目，将下载的压缩包中必要的文件拷入项目中(如图所示)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span><img class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/34d26600bdb98c5b738da5b1.jpg\" alt=\"\" /></span></p><p><span>&nbsp;&nbsp;&nbsp;&nbsp;3. 创建一个一般处理程序UploadHander.ashx(HttpHander)用于处理上传的文件</span></p><p><span>&nbsp;&nbsp;&nbsp; 4. 创建UploadFile文件夹用来保存上传的图片</span></p><p><span>&nbsp;&nbsp;&nbsp; 5. 修改Default.aspx的HTML代码</span><span>&nbsp;&nbsp;&nbsp; </span></p><p><span><span><img style=\"width: 580px; height: 41.8071px;\" class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/87426c1a4d7882418618bfb1.jpg\" height=\"41\" width=\"580\" alt=\"\" /></span><br /></span><span><img style=\"width: 580px; height: 389.807px;\" class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/3848e97bdb9e30b52e73b3b1.jpg\" height=\"389\" width=\"580\" alt=\"\" /></span><br /><span><img style=\"width: 580px; height: 117.178px;\" class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/902354d5b636fd59a18bb7b1.jpg\" height=\"117\" width=\"580\" alt=\"\" /></span><br />&nbsp;&nbsp;&nbsp; 6. 修改UploadHander.ashx处理上传的图片</p><p><span><img style=\"width: 580px; height: 326.213px;\" class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/b958f739121a937cb8998fb1.jpg\" height=\"326\" width=\"580\" alt=\"\" /></span><br /><span></span>&nbsp;&nbsp;&nbsp; 7. 运行效果</p><p><span><img style=\"width: 580px; height: 156.629px;\" class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/17bc55127d0d1888f7039eb1.jpg\" height=\"156\" width=\"580\" alt=\"\" /></span><br /><span><img style=\"width: 580px; height: 158.396px;\" class=\"blogimg\" src=\"http://hiphotos.baidu.com/shouxin1014/pic/item/dd77bdd1d729ebd7a0ec9cb1.jpg\" height=\"158\" width=\"580\" alt=\"\" /></span><br />&nbsp;&nbsp;&nbsp; </p><p>&nbsp;&nbsp;&nbsp; 上传功能至此已实现，具体参数说明请参考<a target=\"_blank\">官方文档</a>或<a target=\"_blank\">博客园oec2003(水杯)的博客</a>，下面介绍下本人在实现过程中遇到的问题</p><p>&nbsp;&nbsp;&nbsp; </p><p>&nbsp;&nbsp;&nbsp; <strong>1.buttonText</strong> 属性设置中文无效的问题</p><p>&nbsp;&nbsp;&nbsp;&nbsp;原因：uploadify.js \r\n中对使用escape()进行编码，在uploadify.swf中使用unescape()进行解码，js中的escape()方法是采用ISO \r\nLatin字符集对指定的字符串进行编码的，很明显咱们的中文，包括日文，韩文(CJK)都不包括在ISO Latin字符集里面的。</p><p>&nbsp;&nbsp;&nbsp; 解决方法：</p><p>&nbsp;&nbsp;&nbsp; A. 修改插件</p><p>&nbsp;&nbsp;&nbsp; 修改uploadify.js</p><p>&nbsp;&nbsp;&nbsp; 将 <strong>if (settings.buttonText) data.buttonText = escape(settings.buttonText);</strong></p><p><strong>&nbsp;&nbsp;&nbsp; </strong>改为 <strong>if (settings.buttonText) data.buttonText = encodeURI(settings.buttonText);</strong></p><p><strong>&nbsp;&nbsp;&nbsp; </strong>修改uploadify.fla</p><p>&nbsp;&nbsp;&nbsp; 将 <strong>browseBtn.empty.buttonText.text = unescape(param.buttonText);&nbsp;</strong></p><p><strong>&nbsp;&nbsp; &nbsp;</strong>改为 <strong>browseBtn.empty.buttonText.text = decodeURI(param.buttonText);&nbsp;</strong></p><p><strong>&nbsp;&nbsp;&nbsp;</strong> 然后重新生成uploadify.swf并拷入项目中</p><p>&nbsp;&nbsp;&nbsp; B. 使用 <strong>buttonImg</strong> 参数</p><p>&nbsp;&nbsp;&nbsp; 显然buttonImg是用来设置图片按钮的，自己做的图片，没编码当然不会乱码了，不过这个方法，按钮显示会有点慢，还是比较推荐方法A.</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp; <strong>2. </strong>FireFox中 上传图标看不见</p><p>&nbsp;&nbsp;&nbsp; 原因：没有安装Flash非IE内核的插件</p><p>&nbsp;&nbsp;&nbsp; 解决方法：在FireFox中安装最新版本的非IE内核的插件</p><p>&nbsp;&nbsp;&nbsp; </p><p>&nbsp;&nbsp;&nbsp; <strong>3. <strong>onQueueFull </strong></strong>函数提示两次</p><p>&nbsp;&nbsp;&nbsp; 原因：在uploadify.js中 157行已经弹出提示，所以在页面中设置onQueueFull后会弹出两次提示</p><p>&nbsp;&nbsp;&nbsp; 解决方法：将uploadify.js中 <strong>alert(\'The queue is full.&nbsp; The max size is \' + queueSizeLimit + \'.\');</strong>删除或将提示改为中文;</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp; <strong>4.&nbsp;</strong>uploadify.js中的中文乱码(不修改uploadify.js不会出现这种情况)</p><p><strong>&nbsp;&nbsp;&nbsp; </strong>原因：编码不统一，uploadify.js 采用UTF-8编码，ASP.NET环境默认为GB2312编码</p><p>&nbsp;&nbsp;&nbsp; 解决方法：引用 uploadify.js 时添加 charset=\"gb2312\"</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp; <strong>5. </strong>上传中文名称的文件时,Http Error</p><p>&nbsp;&nbsp;&nbsp; 原因：在uploadify.swf中采用utf-8编码并上传图片，ASP.NET默认GB2312编码</p><p>&nbsp;&nbsp;&nbsp; 解决方法：</p><p>&nbsp;&nbsp;&nbsp; A. 在HttpHander处理上传图片时，添加 <strong>context.Response.ContentEncoding = System.Text.Encoding.UTF8;</strong></p><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;</strong>B.&nbsp;在 Web.config 中添加如下代码</p><p>&nbsp;&nbsp;&nbsp; <strong>&lt;globalization fileEncoding=\"UTF-8\" requestEncoding=\"UTF-8\" responseEncoding=\"UTF-8\"/&gt;&nbsp;</strong></p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp; <strong>6. </strong>关于上传成功后图片自动消失的问题</p><p>&nbsp;&nbsp;&nbsp; 原因：在uploadify.js中第48行,有个属性：removeCompleted 该属性默认值为 true;表示自动移除上传成功的文件</p><p>&nbsp;&nbsp;&nbsp; 解决方法：修改 removeCompleted 默认值为 false</p><p>&nbsp;</p><p>&nbsp;&nbsp;&nbsp; 参考资料：</p><p>&nbsp;&nbsp;&nbsp; <a>http://www.cnblogs.com/oec2003/archive/2010/01/06/1640027.html</a></p><p>&nbsp;&nbsp;&nbsp; <a>http://www.bq69.com/browse-scripting/jquery-uploadify-messy-code-button.html</a></p>\r\n            			</div><br />'),(101,'2','Yii cookie使用方法',1,1,1,1,522,1327548969,1396570640,'Cookie,代码',1,'<div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">php代码</div><pre class=\"brush:php;\">//设置cookie:\r\n$cookie = new CHttpCookie(‘mycookie’,’this is my cookie’);<br />$cookie-&gt;expire = time()+60*60*24*30;  //有限期30天Yii::app()-&gt;t;<br />request-&gt;cookies[\'mycookie\']=$cookie;\r\n//读取cookie:\r\n$cookie = Yii::app()-&gt;request-&gt;getCookies();<br />echo $cookie[\'mycookie\']-&gt;value;\r\n//销毁cookie:\r\n$cookie = Yii::app()-&gt;request-&gt;getCookies();<br />unset($cookie[$name]);\r\n</pre></div><br />'),(102,'2','javascript操作cookie',1,1,1,1,313,1327681812,1397078412,' 参数,服务器,时候,浏览器,客户端',1,'问题：<br />&nbsp;&nbsp;&nbsp; 使得在访问页面的时候能够沿用上次的设置，或者在不同的页面间共享数据。比如用户在访问网站的时候设置了页面字体的大小，那么会希望下次访问的时候仍然能使用同样的设置进行浏览，而不用重复设置。<br />解决方案：<br />&nbsp;&nbsp;&nbsp; 在用户浏览页面并进行设置时，将这些设置保存在cookie中，下次访问的时候读取cookie中的设置。<br /><p>&nbsp;&nbsp;&nbsp; 参考下面的脚本</p><p><div style=\"border:solid #ccc 1px;width:98%;overflow:auto;\"><div style=\"background-color:#ddd; border-bottom:solid 1px #ccc; line-height:1.5em; text-indent:0.5em;\">js代码</div><pre class=\"brush:js;\">   // utility function to retrieve an expiration data in proper format;\r\n    function getExpDate(days, hours, minutes)\r\n    {\r\n        var expDate = new Date();\r\n        if(typeof(days) == \"number\" &amp;&amp; typeof(hours) == \"number\" &amp;&amp; typeof(hours) == \"number\")\r\n        {\r\n            expDate.setDate(expDate.getDate() + parseInt(days));\r\n            expDate.setHours(expDate.getHours() + parseInt(hours));\r\n            expDate.setMinutes(expDate.getMinutes() + parseInt(minutes));\r\n            return expDate.toGMTString();\r\n        }\r\n    }\r\n    //utility function called by getCookie()\r\n    function getCookieVal(offset)\r\n    {\r\n        var endstr = document.cookie.indexOf(\";\", offset);\r\n        if(endstr == -1)\r\n        {\r\n            endstr = document.cookie.length;\r\n        }\r\n        return unescape(document.cookie.substring(offset, endstr));\r\n    }\r\n    // primary function to retrieve cookie by name\r\n    function getCookie(name)\r\n    {\r\n        var arg = name + \"=\";\r\n        var alen = arg.length;\r\n        var clen = document.cookie.length;\r\n        var i = 0;\r\n        while(i &lt; clen)\r\n        {\r\n            var j = i + alen;\r\n            if (document.cookie.substring(i, j) == arg)\r\n            {\r\n                return getCookieVal(j);\r\n            }\r\n            i = document.cookie.indexOf(\" \", i) + 1;\r\n            if(i == 0) break;\r\n        }\r\n        return;\r\n    }\r\n    // store cookie value with optional details as needed\r\n    function setCookie(name, value, expires, path, domain, secure)\r\n    {\r\n        document.cookie = name + \"=\" + escape(value) +\r\n            ((expires) ? \"; expires=\" + expires : \"\") +\r\n            ((path) ? \"; path=\" + path : \"\") +\r\n            ((domain) ? \"; domain=\" + domain : \"\") +\r\n            ((secure) ? \"; secure\" : \"\");\r\n    }\r\n    // remove the cookie by setting ancient expiration date\r\n    function deleteCookie(name,path,domain)\r\n    {\r\n        if(getCookie(name))\r\n        {\r\n            document.cookie = name + \"=\" +\r\n                ((path) ? \"; path=\" + path : \"\") +\r\n                ((domain) ? \"; domain=\" + domain : \"\") +\r\n                \"; expires=Thu, 01-Jan-70 00:00:01 GMT\";\r\n        }\r\n    }</pre></div> 使用getCookie(name)函数来读取cookie中保存的值，参数name为cookie项的名称。如果该cookie项不存在则返回一个空字符串。<br />&nbsp;&nbsp;&nbsp; 使用setCookie()函数来保存cookie项的值，其中第一、二两个参数分别为cookie项的名称和值。如果想为其设置一个过期时间，那么就需要设置第三个参数，这里需要通过getExpDate()获得一个正确格式的参数。<br />&nbsp; &nbsp; 最后，使用deleteCookie()来删除一个已存在的cookie项，实际上是通过让该项过期。<br />&nbsp;&nbsp;&nbsp;\r\n \r\ncookie将数据保存在客户端。页面的脚本只能读取所在域和服务器的cookie值，如果域内有多个服务器，那么需要设置第五个参数，以指定服务器。浏\r\n览器的容量一般限定为每服务器20个name/value对，每个cookie项不超过4000个字符，更现实点，单个cookie项应少于2000字\r\n符，也就是说不要用cookie在客户端保存大容量数据。<br />&nbsp;&nbsp;&nbsp; 不同的浏览器保存cookie的方式也有所不同。IE为每个域的cookie建立一个文本文件，而Netscape则将所有的cookie存储在同一个文本文件中。<br />&nbsp;&nbsp;&nbsp; 注意：cookie存放在客户端，所以会受到浏览器设置的影响，比如用户可能会禁用cookie。要检测浏览器是否支持cookie，使用属性navigator.cookieEnabled来判断。<br /><br /><br />&nbsp;&nbsp;&nbsp; 参考： (Oreilly) Java Script And Dhtml Cookbook.chm<br /></p>'),(103,'2','微博心事-2012-02-04',1,0,1,1,378,1328325118,1397134319,NULL,1,'1.Leader领头培养团队激情;2.团队一定有人主外,有人主内;3.团队成员的主人翁意识;4.家庭式办公;5.做好当下,不宜过度思考;6.产品设计人人都是参与者;7.做好自己擅长的事情;8.搞技术的高手都是\"业余\"的;9.工作时发了疯地工作,玩耍时卖力地玩;10. 要有好的外包资源,除了人脉和忽悠能力,还得有好的case。'),(104,'2','19个非常有用的 jQuery 图片滑动插件和教程',1,1,1,1,332,1328492805,1397111279,' 图片,插件,美的,效果,教程',1,'jQuery 是一个非常优秀的 Javascript \r\n框架，使用简单灵活，同时还有许多成熟的插件可供选择。其中，最令人印象深刻的应用之一就是对图片的处理，它可以让帮助你在你的项目中加入精美的效果。今\r\n天这篇文章向大家分享19个非常有用的 jQuery 图片滑动插件和教程。\r\n<h3><a target=\"_blank\" href=\"http://slidesjs.com/\">Slides</a></h3>\r\n<p><img src=\"http://pic002.cnblogs.com/images/2011/36987/2011071501112434.jpg\" alt=\"\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.smoothdivscroll.com/\">Smooth Div Scroll</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1233\" title=\"smoothdivscroll\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/smoothdivscroll.png\" alt=\"\" height=\"195\" width=\"475\" /></p>\r\n<h3><a href=\"http://designm.ag/tutorials/image-rotator-css-jquery/\" target=\"_blank\">Create an Image Rotator with Description</a></h3>\r\n<p><img src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/image-rotator-css-jquery.jpg\" alt=\"\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://buildinternet.com/2010/02/animate-panning-slideshow-with-jquery/\">Animate Panning Slideshow with jQuery</a></h3>\r\n<p><img class=\"size-full wp-image-1216 alignnone\" title=\"panning-slideshow-screen\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/panning-slideshow-screen1.jpg\" alt=\"\" height=\"273\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://sixrevisions.com/tutorials/javascript_tutorial/create-a-slick-and-accessible-slideshow-using-jquery/\" title=\"Permanent Link to Create a Slick and Accessible Slideshow Using jQuery\" rel=\"bookmark\">Create a Slick and Accessible Slideshow</a></h3>\r\n<p><img class=\"size-full wp-image-1214 alignnone\" title=\"Create a Slick and Accessible Slideshow Using jQuery\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/Create-a-Slick-and-Accessible-Slideshow-Using-jQuery1.jpg\" alt=\"\" height=\"244\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.dreamcss.com/2009/04/create-beautiful-jquery-sliders.html\">Create Beautiful jQuery slider tutorial</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1217\" title=\"jQuerysliders10\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/jQuerysliders10.png\" alt=\"\" height=\"190\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.zurb.com/playground/jquery_image_slider_plugin\">Orbit: A Slick jQuery Image Slider Plugin</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1219\" title=\"Orbit\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/Orbit.jpg\" alt=\"\" height=\"222\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://css-tricks.com/creating-a-slick-auto-playing-featured-content-slider/\">Creating a Slick Auto-Playing Featured Content Slider</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1220\" title=\"Creating a Slick Auto-Playing Featured Content Slider\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/Creating-a-Slick-Auto-Playing-Featured-Content-Slider.jpg\" alt=\"\" height=\"386\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.visual-blast.com/javascript/s3slider-jquery-image-gallery-plugin/\">s3Slider – jQuery Image Gallery Plugin</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1221\" title=\"s3-slider-jquery-slideshow-plugin\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/s3-slider-jquery-slideshow-plugin.jpg\" alt=\"\" height=\"304\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.sohtanaka.com/web-design/automatic-image-slider-w-css-jquery/\">Automatic Image Slider w/ CSS &amp; jQuery</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1223\" title=\"automatic image slider\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/automatic-image-slider1.jpg\" alt=\"\" height=\"223\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://aviathemes.com/aviaslider/\">AviaSlider</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1224\" title=\"AviaSlider\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/AviaSlider.png\" alt=\"\" height=\"270\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://tutorialzine.com/2009/11/beautiful-apple-gallery-slideshow/\">A Beautiful Apple-style Slideshow Gallery With CSS &amp; jQuery</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1225\" title=\"apple gallery\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/apple-gallery.jpg\" alt=\"\" height=\"270\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.webdesignbooth.com/step-by-step-to-create-content-slider-using-jflow-a-minimalist-jquery-plugin/\" title=\"Step By Step To Create Content Slider using jFlow, A Minimalist jQuery Plugin\" rel=\"bookmark\">Create Content Slider using jFlow, A Minimalist jQuery Plugin</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1226\" title=\"Image-slider-jquery-tutorial\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/Image-slider-jquery-tutorial.jpg\" alt=\"\" height=\"201\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://jqueryglobe.com/article/feature-list\">Feature List</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1227\" title=\"feature-list\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/feature-list.png\" alt=\"\" height=\"168\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://net.tutsplus.com/tutorials/javascript-ajax/a-bullet-proof-content-viewer/\">A Bullet-Proof Content Viewer</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1228\" title=\"jquery_slider_8\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/jquery_slider_8.jpg\" alt=\"\" height=\"204\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://www.davidmassiani.com/horinaja/\">Horinaja</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1229\" title=\"horinaja\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/horinaja.png\" alt=\"\" height=\"288\" width=\"475\" /></p>\r\n<h3><a href=\"http://www.gcmingati.net/wordpress/wp-content/lab/jquery/svwt/index.html\" target=\"_blank\">SlideViewerPro 1.0</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1230\" title=\"slide-viewer-jquery-image-slideshow-free-tools\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/slide-viewer-jquery-image-slideshow-free-tools.jpg\" alt=\"\" height=\"226\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://workshop.rs/projects/coin-slider/\">COIN SLIDER</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1231\" title=\"Coin-Slider\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/Coin-Slider.png\" alt=\"\" height=\"269\" width=\"475\" /></p>\r\n<h3><a target=\"_blank\" href=\"http://cssglobe.com/post/4004/easy-slider-15-the-easiest-jquery-plugin-for-sliding\">Easy Slider 1.5</a></h3>\r\n<p><img class=\"alignnone size-full wp-image-1218\" title=\"easy slider\" src=\"http://www.webdesignburn.com/wp-content/uploads/2011/06/easy-slider.png\" alt=\"\" height=\"148\" width=\"475\" /></p><br />'),(105,'2','使用jQuery制作滑动动画效果的层 ',1,1,1,1,260,1328493077,1396865139,' 效果,需要,部分,文章,实例',1,'<p><strong>基本原理</strong></p>\r\n<p>这些具有动态效果的滑动盒都基于同样的基本原理。在你经过想要\"窥见\"对象中的其他两个项目，这个带有\".boxgrid\"的DIV标签充当着一个窗口。还不明白? 让这个图片来给你线索吧：</p>\r\n<p align=\"center\"><img src=\"http://www.blueidea.com/articleimg/2009/11/7201/01.jpg\" border=\"0\" height=\"221\" width=\"510\" alt=\"\" /></p>\r\n<p>理解了这个基本原理之后，我们就可以利用滑动元素的动画效果来揭开或遮盖住要展示的区域，以此来创造滑动效果。</p>\r\n<p align=\"center\"><a href=\"http://www.blueidea.com/articleimg/2009/11/7201/index.htm\" target=\"_blank\"><strong>点击预览效果预览</strong></a><strong>　</strong><a href=\"http://www.blueidea.com/articleimg/2009/11/7201/slidingboxes.zip\" target=\"_blank\"><strong>下载这个实例的源文件</strong></a><strong> </strong></p>\r\n<p><strong>第一步 – CSS 基础工作</strong></p>\r\n<p>在上面给出基本结构的启示图中，我们需要使用一点CSS来让它显示出预期的效果。下面这个CSS定义了查看窗口(.boxgrid) \r\n并的在LEFT和TOP设定图片的默认POSITION，这对于滑动时的重叠交代很重要。并且不要忘记overflow:hidden将使这一切成为可\r\n能。</p>\r\n<p><span class=\"code\">.boxgrid{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; width: 325px;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; height: 260px;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; margin:10px;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; float:left;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; background:#161613;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; border: solid 2px #8399AF;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; overflow: hidden;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; position: relative;<br />}<br />.boxgrid img{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; position: absolute;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top: 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left: 0;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; border: 0;<br />}</span></p>\r\n<p>如果你不准备用CSS来实现半透明的描述，可直接跳向第二步：</p>\r\n<p><span class=\"code\">.boxcaption{<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; float: left;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; position: absolute;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; background: #000;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; height: 100px;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; width: 100%;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; opacity: .8;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /* For IE 5-7 */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; filter: progid:DXImageTransform.Microsoft.Alpha(Opacity=80);<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; /* For IE 8 */<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -MS-filter: \"progid:DXImageTransform.Microsoft.Alpha(Opacity=80)\";<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</span></p>\r\n<p>现在，我们需要设定说明层（caption \r\nbox，觉得用层好过于盒）的默认的起点。如果想让其初始化的时候完全隐藏，这将需要设定TOP和LEFT为你的窗口(.boxgrid)的高和宽，（当\r\n然）这是由所要滑动的方向决定的。你也可以让它在初始化的时候只显示一部分，像这个（CSS所定义的）.caption和.boxcaption所给出\r\n的：</p>\r\n<p><span class=\"code\">&nbsp;.captionfull .boxcaption {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top: 260;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left: 0;<br />&nbsp;}<br />&nbsp;.caption .boxcaption {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; top: 220;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; left: 0;<br />&nbsp;}</span></p><p><strong>第二步 – 添加滑动动画</strong></p>\r\n<p>下一步是选择适合你的动画，我提供了几个预设的潜在需求（样式）。试用一下他们，选择一个符合你风格也是你需要的样式。</p>\r\n<p><span class=\"code\">$(document).ready(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //要更改上、下方向和左、右方向，只需要在top/left的值中添加\"-\"号（表示反方面）。<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //垂直滑动<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'.boxgrid.slidedown\').hover(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'-260px\'},{queue:false,duration:300});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, function() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'0px\'},{queue:false,duration:300});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //水平没去<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'.boxgrid.slideright\').hover(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({left:\'325px\'},{queue:false,duration:300});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, function() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({left:\'0px\'},{queue:false,duration:300});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //比例缩放滑动<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'.boxgrid.thecombo\').hover(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'260px\', left:\'325px\'},{queue:false,duration:300});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, function() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'0px\', left:\'0px\'},{queue:false,duration:300});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //部分滑动 (只显示一部分背景)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'.boxgrid.peek\').hover(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'90px\'},{queue:false,duration:160});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, function() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'0px\'},{queue:false,duration:160});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //完全滑动的说明 (从完全隐藏到完全显示)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'.boxgrid.captionfull\').hover(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'160px\'},{queue:false,duration:160});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, function() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'260px\'},{queue:false,duration:160});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //部分滑动的说明 (部分显示-部分隐藏)<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\'.boxgrid.caption\').hover(function(){<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'160px\'},{queue:false,duration:160});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }, function() {<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(\".cover\", this).stop().animate({top:\'220px\'},{queue:false,duration:160});<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br />});</span></p>\r\n<p><strong>第三步 – HTML</strong></p>\r\n<p>这里需要一些类（class）来作为jQuery的选择器，在心里存着这样的标准：</p>\r\n<ul><li>DIV标签的类\".cover\"必须分配给任何一个想要滑动的对象； \r\n</li><li>有.boxgrid类的DIV标签，图片通常最先显示。 </li></ul>\r\n<p>这是一个我将用在.captionfull动画的HTML例子：</p>\r\n<p><span class=\"code\">&lt;div class=\"boxgrid captionfull\"&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;img src=\"jareck.jpg\"/&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;div class=\"cover boxcaption\"&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;h3&gt;Jarek Kubicki&lt;/h3&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;p&gt;Artist&lt;br/&gt;&lt;a href=\"<a href=\"http://www.nonsensesociety.com/2009/03/art-by-jarek-kubicki/\">http://www.nonsensesociety.com/2009/03/art-by-jarek-kubicki/</a>\" target=\"_BLANK\"&gt;More Work&lt;/a&gt;&lt;/p&gt;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &lt;/div&gt;<br />&lt;/div&gt;</span></p>\r\n<p>好吧。文章标题就这样被我改了。不过，这样对于中文更好理解。而文章这最后一段就不用全译了。只要你知道，这些实例只是供你选择，要创造出一个适合自己的，就要自己动手。如果你有什么不懂的，欢迎到<a href=\"http://www.techmeme.org.cn/\" target=\"_blank\">Tech Meme论坛</a>来讨论。这里面没有很多人，因为需要邀请。但，大家喜欢讨论技术问题。如果你需要进来讨论，请<a href=\"http://www.happinesz.cn/contact-me/\" target=\"_blank\">联系我</a>。不讨论的就不用邀请码了，因为你可以看到里面的所有文章，除了下载资料</p>\r\n<p align=\"center\"><a href=\"http://www.blueidea.com/articleimg/2009/11/7201/index.htm\" target=\"_blank\"><strong>点击预览效果预览</strong></a><strong>　</strong><a href=\"http://www.blueidea.com/articleimg/2009/11/7201/slidingboxes.zip\" target=\"_blank\"><strong>下载这个实例的源文件</strong></a><strong> </strong></p>\r\n<p><strong>译文原文</strong>：<a href=\"http://www.happinesz.cn/archives/1167/\" target=\"_blank\">http://www.happinesz.cn/archives/1167/</a></p><br />'),(106,'2','49个jQuery代码经典片段',1,1,1,1,236,1328494545,1397111279,' 元素,事件,方法,用法,全局',1,'49个jquery代码经典片段，这些代码能够给你的javascript项目提供帮助。其中的一些代码段是从jQuery1.4.2才开始支持的做法，\r\n另一些则是真正有用的函数或方法，他们能够帮助你又快又好地把事情完成。如果你发现你任何可以做得更好的地方的话，欢迎把你的版本粘贴在评论中!<br />\r\n<br />\r\n1. \r\n如何修改jQuery默认编码（例如默认UTF-8改成改GB2312）：<span style=\"color:#000000;\">$.ajaxSetup({<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; \r\najaxSettings:{ contentType:\"application/x-www-form-urlencoded;chartset=GB2312\"} <br />\r\n});<br />\r\n</span><br />\r\n<br />\r\n2. 解决jQuery, prototype共存，$全局变量冲突问题：<br />\r\n<span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">script src</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">prototype.js</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">&gt;&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">script&gt;</span><span style=\"color:#000000;\"><br />\r\n</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">script src</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">http://blogbeta.blueidea.com/jquery.js</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">&gt;&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">script&gt;</span><span style=\"color:#000000;\"><br />\r\n</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">script \r\ntype</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">text/javascript</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"><br />\r\n&nbsp; &nbsp;&nbsp; \r\n&nbsp;jQuery.noConflict();<br />\r\n</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">script&gt; </span><br />\r\n<span style=\"color:#ff00;\">注意：一定要先引入prototype.js 再引入jquery.js，先后顺序不可错。</span><br />\r\n<br />\r\n3. jQuery \r\n判断元素上是否绑定了事件<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">jQuery \r\nevent封装支持判断元素上是否绑定了事件，此方法只适用于jQuery绑定的事件</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> $events </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">#foo</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).data(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">events</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">);<br />\r\n</span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">( $events </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> $events[</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">click</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">] ){<br />\r\n　　</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">your \r\ncode</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">} </span><br />\r\n<br />\r\n4. \r\n如何使用jQuery来切换样式表<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">找出你希望切换的媒体类型（media-type），然后把href设置成新的样式表。</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">link[media=</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">screen</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).attr(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">href</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, </span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">alternative.css</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">);</span><br />\r\n<br />\r\n5. \r\n如何限制选择范围（基于优化目的）：<br />\r\n<br />\r\n<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">尽可能使用标签名来作为类名的前缀，</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">这样jQuery就不需要花费更多的时间来搜索</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">你想要的元素。还要记住的一点是，</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">针对于你的页面上的元素的操作越具体化，</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">就越能降低执行和搜索的时间。</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> in_stock </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#shopping_cart_items input.is_in_stock</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">);<br />\r\n<br />\r\n</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">ul id</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">shopping_cart_items</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"><br />\r\n　　</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">li</span><span style=\"color:#000000;\">&gt;&lt;</span><span style=\"color:#000000;\">input type</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">radio</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> value</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">Item-X</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> name</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">item</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nclass</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">is_in_stock</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">&gt; Item X&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">li</span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"><br />\r\n　　</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">li</span><span style=\"color:#000000;\">&gt;&lt;</span><span style=\"color:#000000;\">input \r\ntype</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">radio</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nvalue</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">Item-Y</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nname</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">item</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nclass</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">3-5_days</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">&gt; Item Y&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">li</span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"><br />\r\n　　</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">li</span><span style=\"color:#000000;\">&gt;&lt;</span><span style=\"color:#000000;\">input \r\ntype</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">radio</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nvalue</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">Item-Z</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nname</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">item</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> \r\nclass</span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">unknown</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">&gt; Item Z&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">li</span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"><br />\r\n</span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">ul&gt;</span><br />\r\n<br />\r\n6. 如何正确地使用toggleClass：<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">切换（toggle）类允许你根据某个类的</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">是否存在来添加或是删除该类。</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">这种情况下有些开发者使用：</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">a.hasClass(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">blueButton</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">) </span><span style=\"color:#000000;\">?</span><span style=\"color:#000000;\"> a.removeClass(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">blueButton</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">) : \r\na.addClass(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">blueButton</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">);<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">toggleClass允许你使用下面的语句来很容易地做到这一点</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">a.toggleClass(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">blueButton</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">);</span><br />\r\n<br />\r\n7. 如何设置IE特有的功能：<br />\r\n<span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\"> ($.browser.msie) {<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\"> Internet Explorer就是个虐待狂</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">}</span><br />\r\n<br />\r\n8. \r\n如何使用jQuery来代替一个元素：<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#thatdiv</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).replaceWith(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">fnuh</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">);</span><br />\r\n<br />\r\n9. 如何验证某个元素是否为空：<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\"> 方法一</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\"> (! $(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#keks</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).html()) {<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">什么都没有找到;</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">}<br />\r\n<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\"> \r\n方法二</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\"> ($(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#keks</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).is(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">:empty</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">)) \r\n{<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">什么都没有找到;</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">}</span><br />\r\n<br />\r\n10. \r\n如何从一个未排序的集合中找出某个元素的索引号<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">ul &gt; li</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).click(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\"> () {<br />\r\n</span><span style=\"color:#00ff;\">　　var</span><span style=\"color:#000000;\"> index </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).prevAll().length; </span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">prevAll([expr]): 查找当前元素之前所有的同辈元素</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">});</span><br />\r\n<br />\r\n11. \r\n如何把函数绑定到事件上：<br />\r\n<br />\r\n<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">方法一</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#foo</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).click(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(event) { <br />\r\n　　alert(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">User clicked on \"foo.\"</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); <br />\r\n}); <br />\r\n<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">方法二, 支持动态传参</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#foo</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).bind(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">click</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, \r\n{test1:</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">abc</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, test2:</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">123</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">}, </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(event) { <br />\r\n　　alert(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">User clicked on \r\n\"foo.\"</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">+</span><span style=\"color:#000000;\"> event.data.test1 </span><span style=\"color:#000000;\">+</span><span style=\"color:#000000;\"> event.data.test2 ); <br />\r\n}); </span><br />\r\n<br />\r\n12. 如何追加或是添加html到元素中：<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#lal</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).append(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">sometext</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); </span><br />\r\n<br />\r\n13. 在创建元素时，如何使用对象字面量（literal）来定义属性<br />\r\n<span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> e </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\"\"</span><span style=\"color:#000000;\">, { href: </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">#</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, class: </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">a-class another-class</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, title: </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">...</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> }); </span><br />\r\n<br />\r\n14. 如何使用多个属性来进行过滤<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">在使用许多相类似的有着不同类型的input元素时， </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">这种基于精确度的方法很有用 </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> elements </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#someid \r\ninput[type=sometype][value=somevalue]</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).get(); </span><br />\r\n<br />\r\n15. 如何使用jQuery来预加载图像：<br />\r\n<span style=\"color:#000000;\">jQuery.preloadImages </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">() { <br />\r\n</span><span style=\"color:#00ff;\">　　for</span><span style=\"color:#000000;\">(</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> i </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">0</span><span style=\"color:#000000;\">; i </span><span style=\"color:#000000;\">&lt;</span><span style=\"color:#000000;\"> arguments.length; i</span><span style=\"color:#000000;\">++</span><span style=\"color:#000000;\">) { <br />\r\n　　　　$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">&lt;img /&gt;</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).attr(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">src</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, arguments<em>); <br />\r\n　　}<br />\r\n}; <br />\r\n</em></span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">用法 </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$.preloadImages(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">image1.gif</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, </span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">/path/to/image2.png</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, </span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">some/image3.jpg</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); </span><br />\r\n<br />\r\n16. 如何为任何与选择器相匹配的元素设置事件处理程序：<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">button.someClass</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).live(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">click</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, \r\nsomeFunction); <br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">注意，在jQuery 1.4.2中，delegate和undelegate选项 </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">被引入代替live，因为它们提供了更好的上下文支持 </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">例如，就table来说，以前你会用</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">table</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).each(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){ <br />\r\n　　$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">td</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).live(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">hover</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){ <br />\r\n　　　　$(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).toggleClass(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">hover</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">); <br />\r\n　　}); <br />\r\n}); <br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">现在用 </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">table</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).delegate(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">td</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">hover</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">, </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){ <br />\r\n　　$(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).toggleClass(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">hover</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">); <br />\r\n}); </span><br />\r\n<br />\r\n17. 如何找到一个已经被选中的option元素：<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#someElement</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).find(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">option:selected</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); </span><br />\r\n<br />\r\n18. 如何隐藏一个包含了某个值文本的元素：<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">p.value:contains(\'thetextvalue\')</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).hide(); </span><br />\r\n<br />\r\n19. \r\n如何创建嵌套的过滤器：<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">允许你减少集合中的匹配元素的过滤器，</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">只剩下那些与给定的选择器匹配的部分。在这种情况下，</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">查询删除了任何没（:not）有（:has）</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">包含class为“selected”（.selected）的子节点。</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">.filter(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">:not(:has(.selected))</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">)</span><br />\r\n<br />\r\n20. \r\n如何检测各种浏览器：<br />\r\n<br />\r\n检测Safari (if( $.browser.safari)),&nbsp;&nbsp;<br />\r\n检测IE6及之后版本 (if \r\n($.browser.msie &amp;&amp; $.browser.version &gt; 6 )),&nbsp;&nbsp;<br />\r\n检测IE6及之前版本 (if \r\n($.browser.msie &amp;&amp; $.browser.version &lt;= 6 )),&nbsp;&nbsp;<br />\r\n检测FireFox 2及之后版本 \r\n(if ($.browser.mozilla &amp;&amp; $.browser.version &gt;= \'1.8\' ))<br />\r\n21. \r\n任何使用has()来检查某个元素是否包含某个类或是元素：<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">jQuery 1.4.*包含了对这一has方法的支持。</span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\">该方法找出某个元素是否包含了其他另一个元素类或是其他任何的你正在查找并要在其之上进行操作的东东。</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">input</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).has(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">.email</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).addClass(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">email_icon</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">);</span><br />\r\n<br />\r\n22. 如何禁用右键单击上下文菜单：<br />\r\n<span style=\"color:#000000;\">$(document).bind(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">contextmenu</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">,</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(e){ <br />\r\n　　</span><span style=\"color:#00ff;\">return</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">false</span><span style=\"color:#000000;\">; <br />\r\n}); </span><br />\r\n<br />\r\n23. \r\n如何定义一个定制的选择器<br />\r\n<span style=\"color:#000000;\">$.expr[</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">:</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">].mycustomselector </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(element, index, meta, stack){ <br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\"> element- 一个DOM元素 </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\"> index – 栈中的当前循环索引 </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\"> meta – 有关选择器的元数据 </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\"> stack – 要循环的所有元素的栈 </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\"> 如果包含了当前元素就返回true </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\"> 如果不包含当前元素就返回false }; </span><span style=\"color:#0800;\"><br />\r\n//</span><span style=\"color:#0800;\"> 定制选择器的用法： </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">.someClasses:test</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).doSomething(); </span><br />\r\n<br />\r\n24. 如何检查某个元素是否存在<br />\r\n<span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\"> ($(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#someDiv</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).length) { <br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">万岁！！！它存在…… </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">} </span><br />\r\n<br />\r\n25. \r\n如何使用jQuery来检测右键和左键的鼠标单击两种情况：<br />\r\n<br />\r\n<br />\r\n<span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">#someelement</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).live(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">click</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(e) { <br />\r\n&nbsp; &nbsp; </span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">( (</span><span style=\"color:#000000;\">!</span><span style=\"color:#000000;\">$.browser.msie </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> e.button </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">0</span><span style=\"color:#000000;\">) </span><span style=\"color:#000000;\">||</span><span style=\"color:#000000;\"> ($.browser.msie </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> e.button </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">1</span><span style=\"color:#000000;\">) ) { <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;alert(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">Left Mouse Button Clicked</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">); <br />\r\n&nbsp; &nbsp; } </span><span style=\"color:#00ff;\">else</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">(e.button </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">2</span><span style=\"color:#000000;\">) { <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;alert(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">Right Mouse Button Clicked</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">); <br />\r\n&nbsp; &nbsp; }<br />\r\n});</span><br />\r\n<br />\r\n26. 如何替换串中的词<br />\r\n<span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> el </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#id</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); <br />\r\nel.html(el.html().replace(</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">word</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">ig, </span><span style=\"color:#000000;\">\'\'</span><span style=\"color:#000000;\">)); </span><br />\r\n<br />\r\n27. \r\n如何在一段时间之后自动隐藏或关闭元素（支持1.4版本）：<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">这是1.3.2中我们使用setTimeout来实现的方式 </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">setTimeout(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">() { <br />\r\n$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">.mydiv</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).hide(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">blind</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, {}, </span><span style=\"color:#000000;\">500</span><span style=\"color:#000000;\">) <br />\r\n}, </span><span style=\"color:#000000;\">5000</span><span style=\"color:#000000;\">); <br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">而这是在1.4中可以使用delay()这一功能来实现的方式（这很像是休眠） </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">.mydiv</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).delay(</span><span style=\"color:#000000;\">5000</span><span style=\"color:#000000;\">).hide(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">blind</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, {}, </span><span style=\"color:#000000;\">500</span><span style=\"color:#000000;\">); </span><br />\r\n<br />\r\n28. \r\n如何把已创建的元素动态地添加到DOM中：<br />\r\n<span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> newDiv </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">&lt;div&gt;&lt;/div&gt;</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); <br />\r\nnewDiv.attr(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">id</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">,</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">myNewDiv</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).appendTo(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">body</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">); </span><br />\r\n<br />\r\n29. 如何限制“Text-Area”域中的字符的个数：<br />\r\n<span style=\"color:#000000;\">jQuery.fn.maxLength </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(max){ <br />\r\n&nbsp; &nbsp; </span><span style=\"color:#00ff;\">return this</span><span style=\"color:#000000;\">.each(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){<br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> type </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.tagName.toLowerCase(); <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> inputType </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.type</span><span style=\"color:#000000;\">?</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.type.toLowerCase() : </span><span style=\"color:#00ff;\">null</span><span style=\"color:#000000;\">; <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">(type </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">input</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> inputType </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">text</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">||</span><span style=\"color:#000000;\"> inputType </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">password</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">){ <br />\r\n&nbsp; &nbsp;&nbsp; \r\n&nbsp;&nbsp; &nbsp;&nbsp; &nbsp;</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">Apply the standard \r\nmaxLength </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; \r\n&nbsp;</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.maxLength </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> max; <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;} </span><span style=\"color:#00ff;\">else</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">(type </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">textarea</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">){<br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.onkeypress </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(e){ <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; </span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> ob </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> e </span><span style=\"color:#000000;\">||</span><span style=\"color:#000000;\"> event; <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; </span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> keyCode </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> \r\nob.keyCode; <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; </span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> hasSelection </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> \r\ndocument.selection</span><span style=\"color:#000000;\">?</span><span style=\"color:#000000;\"> \r\ndocument.selection.createRange().text.length </span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">0</span><span style=\"color:#000000;\"> : </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.selectionStart </span><span style=\"color:#000000;\">!=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.selectionEnd; <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; \r\n&nbsp;&nbsp; &nbsp;&nbsp; &nbsp; </span><span style=\"color:#00ff;\">return</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">!</span><span style=\"color:#000000;\">(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.value.length </span><span style=\"color:#000000;\">&gt;=</span><span style=\"color:#000000;\"> \r\nmax </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> (keyCode </span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">50</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">||</span><span style=\"color:#000000;\"> keyCode </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">32</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">||</span><span style=\"color:#000000;\"> keyCode </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">0</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">||</span><span style=\"color:#000000;\"> keyCode </span><span style=\"color:#000000;\">==</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">13</span><span style=\"color:#000000;\">) </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">!</span><span style=\"color:#000000;\">ob.ctrlKey </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">!</span><span style=\"color:#000000;\">ob.altKey </span><span style=\"color:#000000;\">&amp;&amp;</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">!</span><span style=\"color:#000000;\">hasSelection); <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;}; <br />\r\n&nbsp; &nbsp;&nbsp; \r\n&nbsp;&nbsp; &nbsp;&nbsp; &nbsp;</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.onkeyup </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){ <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; </span><span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.value.length </span><span style=\"color:#000000;\">&gt;</span><span style=\"color:#000000;\"> \r\nmax){ <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.value </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.value.substring(</span><span style=\"color:#000000;\">0</span><span style=\"color:#000000;\">,max); <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp; } <br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; &nbsp;&nbsp; \r\n&nbsp;};<br />\r\n&nbsp; &nbsp;&nbsp; &nbsp;&nbsp;&nbsp;}<br />\r\n&nbsp; &nbsp; });<br />\r\n};<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">用法 </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#mytextarea</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).maxLength(</span><span style=\"color:#000000;\">500</span><span style=\"color:#000000;\">); </span><br />\r\n<br />\r\n30. 如何jQuery注册和禁用jQuery全局事件<br />\r\n<span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">jQuery注册ajax全局事件ajaxStart，ajaxStop：</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(document).ajaxStart(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){<br />\r\n&nbsp; &nbsp; $(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">#background,#progressBar</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).show();<br />\r\n}).ajaxStop(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){<br />\r\n&nbsp; &nbsp; $(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">#background,#progressBar</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).hide();<br />\r\n});<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">ajax请求禁用全局事件：$.ajax() 有个参数global (默认: \r\ntrue) 是否触发全局 AJAX 事件.设置为 false 将不会触发全局 AJAX 事件，如 ajaxStart 或 ajaxStop 可用于控制不同的 \r\nAjax 事件。</span><br />\r\n<br />\r\n31. 如何在jQuery中克隆一个元素：<br />\r\n<span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> cloned </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">#somediv</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">).clone(); </span><br />\r\n<br />\r\n32. 在jQuery中如何测试某个元素是否可见<br />\r\n<br />\r\n<br />\r\n<span style=\"color:#00ff;\">if</span><span style=\"color:#000000;\">($(element).is(</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">:visible</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">)) { <br />\r\n</span><span style=\"color:#0800;\">　　//</span><span style=\"color:#0800;\">该元素是可见的 </span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">} </span><br />\r\n<br />\r\n33. \r\n如何把一个元素放在屏幕的中心位置：<br />\r\n<span style=\"color:#000000;\">jQuery.fn.center </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\"> () { <br />\r\n　　</span><span style=\"color:#00ff;\">return</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.each(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){<br />\r\n　　　　$(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).css({<br />\r\n　　　　　　position:</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">absolute</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">,<br />\r\n　　　　　　top, ( $(window).height() </span><span style=\"color:#000000;\">-</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.height() ) </span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">2</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">+</span><span style=\"color:#000000;\"> \r\n$(window).scrollTop() </span><span style=\"color:#000000;\">+</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">px</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">, <br />\r\n　　　　　　left, ( $(window).width() </span><span style=\"color:#000000;\">-</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.width() ) </span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">2</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">+</span><span style=\"color:#000000;\"> \r\n$(window).scrollLeft() </span><span style=\"color:#000000;\">+</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\">px</span><span style=\"color:#000000;\">\'</span><span style=\"color:#000000;\"><br />\r\n　　　　});<br />\r\n　　});<br />\r\n}<br />\r\n</span><span style=\"color:#0800;\">//</span><span style=\"color:#0800;\">这样来使用上面的函数：&nbsp;&nbsp;</span><span style=\"color:#0800;\"><br />\r\n</span><span style=\"color:#000000;\">$(element).center(); </span><br />\r\n34. \r\n如何把有着某个特定名称的所有元素的值都放到一个数组中：<br />\r\n<span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> \r\narrInputValues </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">new</span><span style=\"color:#000000;\"> Array(); <br />\r\n$(</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">input[name=\'xxx\']</span><span style=\"color:#000000;\">\"</span><span style=\"color:#000000;\">).each(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">(){ <br />\r\n　　arrInputValues.push($(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).val());<br />\r\n}); </span><br />\r\n<br />\r\n35. 如何从元素中除去HTML<br />\r\n<span style=\"color:#000000;\">(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">($) { <br />\r\n$.fn.stripHtml </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">() { <br />\r\n　　</span><span style=\"color:#00ff;\">var</span><span style=\"color:#000000;\"> regexp </span><span style=\"color:#000000;\">=</span><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">&lt;(\"[^\"]*\"|\'[^\']*\'|[^\'\"&gt;])*&gt;</span><span style=\"color:#000000;\">/</span><span style=\"color:#000000;\">gi; <br />\r\n　　</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">.each(</span><span style=\"color:#00ff;\">function</span><span style=\"color:#000000;\">() { <br />\r\n　　　　$(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).html( $(</span><span style=\"color:#00ff;\">this</span><span style=\"color:#000000;\">).html().replace(regexp,</span><span style=\"color:#000000;\">\'\'</span><span style=\"color:#000000;\">) ); <br />\r\n　　});<br />\r\n　　</span><span style=\"color:#00ff;\">return</span><span style=\"color:#000000;\"> $(</span><span style=\"color:#00ff;